import React, { Component } from 'react'
import { CookiesProvider, withCookies } from 'react-cookie'
import { Card, Input, Button, Col } from 'antd'

class Monitor extends Component {
    constructor() {
        super();
        this.state = {
            checkLogin: "",
            cook: []
        }
    }

    componentDidMount() {

    }

    Login = () => {
        const { cookies } = this.props;
        cookies.remove('user')
        cookies.remove('pass')
        let user = document.getElementById('user').value,
            pass = document.getElementById('pass').value
        console.log(user)
        cookies.set('user', user)
        cookies.set('pass', pass)
        //console.log(this.props.cookies.cookies)
    }

    delCook = () => {
        const { cookies } = this.props;
        cookies.remove()
    }
    render() {
        let checkLogin = this.state.checkLogin
        if (checkLogin === "") {
            return (
                <div>
                    <Col lg={{ span: 10, offset: 7 }} style={{ padding: "100px 0px 0px 0px" }}>
                        <Card>
                            <h1 style={{ textAlign: "center" }}>Appointment_Login</h1>
                            <h2>UserName:</h2>
                            <Input id="user" />
                            <h2>Password:</h2>
                            <Input id="pass" />
                            <br />
                            <br />
                            <Button block size="large" type="primary" onClick={this.Login} >LOGIN</Button>
                            <br />
                            <br />
                            <Button block size="large" type="danger" >LOGOUT</Button>
                        </Card>
                    </Col>
                </div>
            )
        }
        else if (checkLogin === "b") {
            return (
                <div>
                    <CookiesProvider>
                        <div>
                            <a onClick={this.delCook}>ลบ</a>

                        </div>
                    </CookiesProvider>
                </div>

            )
        }
    }
}

export default withCookies(Monitor)
