import React, { Component } from 'react'
import { Card } from 'antd'
import '../Style/Tables.css'
import moment from 'moment'
class MassageAdmin extends Component {

    constructor() {
        super()
        this.state = {
            massageData: []
        }
    }

    componentDidMount() {
        let year = new Date().getFullYear(),
            month = year + '-' + (new Date().getMonth() + 1),
            day = month + '-' + (new Date().getDate())

        fetch("http://183.88.219.85:7078/appoint/massage_table.php", {
            method: "POST",
            body: JSON.stringify({
                date: day
            })
        })
            .then(res => res.json())
            .then(res => {
                this.setState({
                    massageData: res
                })
            })
    }
    seletTime = (e) => {
        console.log(e)
        let type = e.map(a => a.stat)
        console.log(type)

        switch (type) {
            case [true]:
                alert("แก้ไข")
                break;
            case [false]:
                alert("ทำรายการนัด")
                break;
            default:
                break;
        }
    }


    render() {
        return (
            <div >
                <Card style={{ width: "100%", overflowX: "auto" }}>
                    <table className="Table-Main">
                        {this.state.massageData.map(a => (
                            <tr>
                                <td className="Table-collapse Table-Name">{a.doctor_name_prefex} {a.doctor_name_fname} {a.doctor_name_lname}</td>
                                {a.service.map(b => (
                                    <td className="Table-collapse Table-Time" onClick={() => this.seletTime([{
                                        docId: a.doctor_name_id, pointId: a.point_id, timeId: b.timeId, stat: b.dis, serviceId: b.serviceId
                                    }])}>
                                        <p>{b.time}</p>
                                        <p>{b.stat}</p>
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </table>
                </Card>
            </div >
        )
    }
}
export default MassageAdmin
