import React, { Component } from 'react'
import { Card, Modal, Select, Input } from 'antd'
import '../Style/Tables.css'
import api1 from '../api/fetch'
const { Option } = Select
const { Search } = Input
class MassageAdmin extends Component {

    constructor() {
        super()
        this.state = {
            massageService: [],
            timeSelect: [],
            massageData: [],
            modalAdd: false,
            modalEdit: false
        }
    }

    componentDidMount() {
        api1()
        let date = new Date()
        date = `${date.getFullYear()}-${("0" + (date.getMonth() + 1)).slice(-2)}-${("0" + (date.getDate())).slice(-2)}`
        console.log(date)
        fetch("http://183.88.219.85:7078/appoint/service.php", {
            method: "POST",
            body: JSON.stringify({
                point: 2
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    massageService: res
                })
            })
        fetch("http://183.88.219.85:7078/appoint/massage_table.php", {
            method: "POST",
            body: JSON.stringify({
                date: date
            })
        })
            .then(res => res.json())
            .then(res => {
                this.setState({
                    massageData: res
                })
            })
    }
    seletTime = (e) => {
        console.log(e)
        let type = e.map(a => a.stat).toString()
        this.setState({
            timeSelect: e
        })
        console.log(type)

        switch (type) {
            case "true":
                this.setState({
                    modalEdit: true
                })
                break;
            case "false":
                this.setState({
                    modalAdd: true
                })

                break;
            default:
                break;
        }
    }

    searchHn = (e) => {
        console.log(e)
    }

    render() {
        return (
            <div >
                <Card style={{ width: "100%", overflowX: "auto" }}>
                    <table className="Table-Main">
                        {this.state.massageData.map(a => (
                            <tr className="Table-tr">
                                <td className="Table-collapse Table-Name Table-td">{a.doctor_name_prefex} {a.doctor_name_fname} {a.doctor_name_lname}</td>
                                {a.service.map(b => (
                                    <td className="Table-collapse Table-Time Table-td" onClick={() => this.seletTime([{
                                        docId: a.doctor_name_id, pointId: a.point_id, timeId: b.timeId, stat: b.dis, serviceId: b.serviceId, docName: a.doctor_name_prefex + ' ' + a.doctor_name_fname + ' ' + a.doctor_name_lname, timeName: b.time, date: b.serviceDate
                                    }])}>
                                        <p>{b.time}</p>
                                        <p>{b.name}</p>
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </table>
                </Card>
                {/*Modal Add*/}
                <Modal
                    title={"ทำรายการนัด"}
                    name={"Add"}
                    visible={this.state.modalAdd}
                    onCancel={() => this.setState({ modalAdd: false })}
                >
                    {this.state.timeSelect.map(a => (
                        <Card>
                            <p>ผู้ให้บริการ {a.docName}</p>
                            <p>วันที่ {a.date}</p>
                            <p>เวลา {a.timeName}</p>
                        </Card>
                    ))}
                    <Card>
                        <p>ค้นหาผู้ป่วย</p>
                        <Search id={"hn"} enterButton={"ค้นหา"} onClick={() => this.searchHn()} />
                        <Select>
                            <Option value="">aa</Option>
                        </Select>
                    </Card>
                </Modal>
                {/*Modal Add*/}

                {/*Modal Edit*/}
                <Modal
                    title={"แก้ไข"}
                    name={"Edit"}
                    visible={this.state.modalEdit}
                    onCancel={() => this.setState({ modalEdit: false })}
                >

                </Modal>
                {/*Modal Edit*/}
            </div >
        )
    }
}
export default MassageAdmin
