import React, { Component } from 'react'
import { Input, Button, Card } from 'antd'
import { CookiesProvider, withCookies } from 'react-cookie'

class Head extends Component {

    constructor() {
        super()
        this.state = {
            loginStat: "1",
            pageManage: "1"
        }
    }

    componentDidMount() {
        const { cookies } = this.props
        //console.log(cookie)
        let userCookie = cookies.get('user'),
            passCookie = cookies.get('pass')

        if (userCookie === undefined || passCookie === undefined) {
            let setLogin = new Promise((resolve, reject) => {
                resolve(
                    this.setState({
                        loginStat: "0"
                    }))
            })
        }
        else {
            fetch("http://183.88.219.85:7078/appoint/login.php", {
                method: "POST",
                body: JSON.stringify({
                    user: userName,
                    pass: passWord
                })
            }).then(res => res.json())
                .then(res => {
                    //console.log(res)
                    let stat = res.map(a => a.stat).toString()
                    if (stat === "1") {
                        this.setState({
                            user: res
                        })
                        this.props.propsLogin(this.state.user)
                    } else {
                        message.warning("usernameหรือpasswordไม่ถูกต้อง")
                    }
                })
        }
    }


    render() {
        return (
            <div>
                <Card >
                    <h2>ระบบนัดออนไลน์</h2>
                </Card>
            </div>
        )
    }
}
export default withCookies(Head)
