import React, { Component } from 'react'
import { Card } from 'antd'
import '../Style/Tables.css'
class MassageAdmin extends Component {

    constructor() {
        super()
        this.state = {
            massageData: []
        }
    }

    componentDidMount() {
        fetch("http://183.88.219.85:7078/appoint/massage_table.php")
            .then(res => res.json())
            .then(res => {
                this.setState({
                    massageData: res
                })
            })
    }
    seletTime = (e) => {
        console.log(e)
    }

    Line = () => {
        let myHeaders = new Headers({
            'Content-Type': 'application/x-www-form-urlencoded',

        })
        fetch("https://notify-api.line.me/api/notify", {
            method: "POST",
            headers: myHeaders,
            auth: {
                bearer: 'Mg0PHoKLtaLOgJJr4GYjmW3x3NOc1Gpzy7IiV56GfOB', //token
            },
            form: {
                message: 'ทดสอบ', //ข้อความที่จะส่ง
            },
        })
    }

    render() {
        return (
            <div >
                <Card style={{ width: "100%", overflowX: "auto" }}>
                    <table className="Table-Main">
                        {this.state.massageData.map(a => (
                            <tr>
                                <td className="Table-collapse Table-Name">{a.doctor_name_prefex} {a.doctor_name_fname} {a.doctor_name_lname}</td>
                                {a.service.map(b => (
                                    <td className="Table-collapse Table-Time" onClick={() => this.seletTime([{
                                        docId: a.doctor_name_id, pointId: a.point_id, timeId: b.timeId, stat: b.dis, serviceId: b.serviceId
                                    }])}>
                                        <p>{b.time}</p>
                                        <p>{b.stat}</p>
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </table>
                </Card>
                <button onClick={this.Line}>line</button>
            </div >)
    }
}
export default MassageAdmin
