import React, { Component } from 'react'
import { Input, Button, Card, message } from 'antd'
import { CookiesProvider, withCookies } from 'react-cookie'
import Login from './Login'
class Head extends Component {

    constructor() {
        super()
        this.state = {
            loginStat: false,
            pageManage: "1",
            user: []
        }
    }

    componentDidMount() {
        const { cookies } = this.props
        //console.log(cookie)
        let userName = cookies.get('user'),
            passWord = cookies.get('pass')

        if (userName === undefined || passWord === undefined) {
            let setLogin = new Promise((resolve, reject) => {
                resolve(
                    this.setState({
                        loginStat: false
                    }))
            })
            setLogin.then(() => {
                this.props.toMonitor(this.state.loginStat)

            })
        }
        else {
            fetch("http://183.88.219.85:7078/appoint/login.php", {
                method: "POST",
                body: JSON.stringify({
                    user: userName,
                    pass: passWord
                })
            }).then(res => res.json())
                .then(res => {
                    //console.log(res)
                    let stat = res.map(a => a.stat).toString()
                    if (stat === "1") {
                        let setLogin = new Promise((resolve) => {
                            resolve(this.setState({
                                user: res,
                                loginStat: true
                            }))
                        })
                        setLogin.then(() => {
                            this.props.toMonitor(this.state.loginStat)
                        })

                        // this.props.propsLogin(this.state.user)
                    } else {
                        message.warning("usernameหรือpasswordไม่ถูกต้อง")
                    }
                })
        }
    }

    componentWillReceiveProps(e) {
        console.log(e)
        this.props.toMonitor(this.state.loginStat)
    }

    render() {
        return (
            <div>
                <Card >
                    <h2>ระบบนัดออนไลน์</h2>
                </Card>
            </div>
        )
    }
}
export default withCookies(Head)
