import React, { Component } from 'react'
import { Select, Button, Card, message } from 'antd'
import { CookiesProvider, withCookies } from 'react-cookie'
import { Redirect } from 'react-router-dom'
import Login from './Login'

const { Option } = Select;
class Monitor extends Component {
    constructor() {
        super()
        this.state = {
            user: [],
            redirects: <html></html>
        }
    }

    componentDidMount() {
        const { cookies } = this.props
        //console.log(cookie)
        let userName = cookies.get('user'),
            passWord = cookies.get('pass')

        if (userName === undefined || passWord === undefined) {
            console.log(userName)
            this.setState({
                redirects: <Redirect to="/login" />
            })
        }
        else {
            fetch("http://183.88.219.85:7078/appoint/login.php", {
                method: "POST",
                body: JSON.stringify({
                    user: userName,
                    pass: passWord
                })
            }).then(res => res.json())
                .then(res => {
                    //console.log(res)
                    let stat = res.map(a => a.stat).toString(),
                        point = res.map(a => a.point).toString()
                    this.setState({
                        user: res
                    })
                    if (stat === "1") {

                    } else {
                        message.warning("usernameหรือpasswordไม่ถูกต้อง")
                    }
                })
        }
    }
    logOut = () => {
        const { cookies } = this.props
        cookies.remove('user')
        cookies.remove('pass')
        let userName = cookies.get('user'),
            passWord = cookies.get('pass')

        if (userName === undefined || passWord === undefined) {
            message.success("LOGOUT SUCCESS")
            this.setState({
                redirects: <Redirect to="/login" />
            })
        }
    }
    render() {
        return (
            <div>
                <Card >
                    <h2>ระบบนัดออนไลน์</h2>
                    <h3>เลือกจุดบริการ</h3>
                    <Button onClick={this.logOut}>Logout</Button>
                    {this.state.redirects}
                </Card>
            </div>
        )
    }
}

export default withCookies(Monitor)
