import React, { Component } from 'react'
import { Card, Modal, Select, Input } from 'antd'
import '../Style/Tables.css'
import { reject } from 'q';
const { Option } = Select
const { Search } = Input
class MassageAdmin extends Component {

    constructor() {
        super()
        this.state = {
            selectService: "",
            person: [],
            massageService: [],
            timeSelect: [],
            massageData: [],
            modalAdd: false,
            modalEdit: false,
        }
    }

    componentDidMount() {

        let date = new Date()
        date = `${date.getFullYear()}-${("0" + (date.getMonth() + 1)).slice(-2)}-${("0" + (date.getDate())).slice(-2)}`
        // console.log(date)
        fetch("http://183.88.219.85:7078/appoint/service.php", {
            method: "POST",
            body: JSON.stringify({
                point: 2
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    massageService: res
                })
            })
        fetch("http://183.88.219.85:7078/appoint/massage_table.php", {
            method: "POST",
            body: JSON.stringify({
                date: date
            })
        })
            .then(res => res.json())
            .then(res => {
                this.setState({
                    massageData: res
                })
            })
    }
    seletTime = (e) => {
        // console.log(e)
        let type = e.map(a => a.stat).toString()
        this.setState({
            timeSelect: e
        })
        // console.log(type)

        switch (type) {
            case "true":
                this.setState({
                    modalEdit: true
                })
                break;
            case "false":
                this.setState({
                    modalAdd: true
                })
                break;
            default:
                break;
        }
    }

    searchHn = (e) => {
        let hn = e
        fetch("http://183.88.219.85:7078/appoint/person_hn.php", {
            method: "POST",
            body: JSON.stringify({
                hn: hn
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    person: res
                })
            })
    }

    selectService = (e) => {
        this.setState({
            selectService: e
        })
    }

    waitingAppoint = () => {
        let person = this.state.person,
            service = [{
                hn: this.state.person.map(a => a.patient_hn).toString(),
                serviceDate: this.state.timeSelect.map(a => a.date).toString(),
                setviceTime: this.state.timeSelect.map(a => a.timeId).toString(),
                servicePlans: this.state.person.map(a => a.contract_plans_description).toString(),
                servicePoint: this.state.timeSelect.map(a => a.pointId).toString(),
                serviceName: this.state.selectService.toString(),
                docId: this.state.timeSelect.map(a => a.docId).toString
            }]

        service.forEach(service => {
            if (service.hn === "" || service.serviceDate === "" || service.setviceTime === "" || service.servicePlans === "" || service.servicePoint === "" || service.serviceName === "" || service.docId === "" || person === []) {
                console.log("ระบุบริการ")
            }
        });
    }
    render() {
        return (
            <div >
                <Card style={{ width: "100%", overflowX: "auto" }}>
                    <table className="Table-Main">
                        {this.state.massageData.map(a => (
                            <tr className="Table-tr">
                                <td className="Table-collapse Table-Name Table-td">{a.doctor_name_prefex} {a.doctor_name_fname} {a.doctor_name_lname}</td>
                                {a.service.map(b => (
                                    <td className="Table-collapse Table-Time Table-td" onClick={() => this.seletTime([{
                                        docId: a.doctor_name_id, pointId: a.point_id, timeId: b.timeId, stat: b.dis, serviceId: b.serviceId, docName: a.doctor_name_prefex + ' ' + a.doctor_name_fname + ' ' + a.doctor_name_lname, timeName: b.time, date: b.serviceDate
                                    }])}>
                                        <p>{b.time}</p>
                                        <p>{b.name}</p>
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </table>
                </Card>
                {/*Modal Add*/}
                <Modal
                    title={"ทำรายการนัด"}
                    name={"Add"}
                    visible={this.state.modalAdd}
                    onCancel={() => this.setState({ modalAdd: false })}
                    onOk={this.waitingAppoint}
                >
                    {this.state.timeSelect.map(a => (
                        <div>
                            <Card>
                                <h3>ผู้ให้บริการ {a.docName}</h3>
                                <h3>วันที่ {a.date}</h3>
                                <h3>เวลา {a.timeName}</h3>
                            </Card>

                            <Card>
                                <p>ค้นหาผู้ป่วย</p>
                                <Search id={"hn"} placeholder={"ค้นหาผู้ป่วยจากHN"} enterButton={"ค้นหา"} onSearch={this.searchHn} />
                                <Card>
                                    {this.state.person.map(b => (
                                        <div>
                                            <h3>HN: {b.patient_hn}</h3>
                                            <h3>ผู้รับบริการ {b.patient_prefix_description} {b.patient_firstname} {b.patient_lastname}</h3>
                                            <h3>อายุ {b.age}</h3>
                                            <h3>สิทธิ์ {b.contract_plans_description}</h3>
                                        </div>
                                    ))}
                                </Card>
                                <Select style={{ width: "100%" }}
                                    placeholder={"ระบุบริการ"}
                                    onSelect={this.selectService}
                                >
                                    {this.state.massageService.map(a => (
                                        <Option value={a.service_name_id}>{a.service_name_order}</Option>
                                    ))}
                                </Select>
                            </Card>
                        </div>
                    ))}
                </Modal>
                {/*Modal Add*/}

                {/*Modal Edit*/}
                <Modal
                    title={"แก้ไข"}
                    name={"Edit"}
                    visible={this.state.modalEdit}
                    onCancel={() => this.setState({ modalEdit: false })}
                >

                </Modal>
                {/*Modal Edit*/}
            </div >
        )
    }
}
export default MassageAdmin
