import React, { Component } from 'react'
import { Input, Button, Card, message } from 'antd'
import { CookiesProvider, withCookies } from 'react-cookie'
import Login from './Login'
class Head extends Component {

    componentDidMount() {
        const { cookies } = this.props
        //console.log(cookie)
        let userName = cookies.get('user'),
            passWord = cookies.get('pass')

        if (userName === undefined || passWord === undefined) {
            let setLogin = new Promise((resolve, reject) => {
                resolve()
            })
            setLogin.then(() => {
                window.location.href = "/MonitorLogin"
            })
        }
        else {
            fetch("http://183.88.219.85:7078/appoint/login.php", {
                method: "POST",
                body: JSON.stringify({
                    user: userName,
                    pass: passWord
                })
            }).then(res => res.json())
                .then(res => {
                    //console.log(res)
                    let stat = res.map(a => a.stat).toString(),
                        point = res.map(a => a.point).toString()
                    if (stat === "1") {

                    } else {
                        message.warning("usernameหรือpasswordไม่ถูกต้อง")
                    }
                })
        }
    }

    logOut = () => {
        const { cookies } = this.props
        cookies.remove('user')
        cookies.remove('pass')
    }
    render() {
        return (
            <div>
                <Card >
                    <h2>ระบบนัดออนไลน์</h2>
                    <Button onClick={this.logOut}>Logout</Button>
                </Card>

            </div>
        )
    }
}



export default withCookies(Head)
