import React, { Component } from 'react'
import { Card, Modal } from 'antd'
import '../Style/Tables.css'
class MassageAdmin extends Component {

    constructor() {
        super()
        this.state = {
            massageService: [],
            timeSelect: [],
            massageData: [],
            modalAdd: false,
            modalEdit: false
        }
    }

    componentDidMount() {
        let year = new Date().getFullYear(),
            month = year + '-' + (new Date().getMonth() + 1),
            day = month + '-' + (new Date().getDate())

        fetch("http://183.88.219.85:7078/appoint/service.php", {
            method: "POST",
            body: JSON.stringify({
                point: 2
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    massageService: res
                })
            })
        fetch("http://183.88.219.85:7078/appoint/massage_table.php", {
            method: "POST",
            body: JSON.stringify({
                date: day
            })
        })
            .then(res => res.json())
            .then(res => {
                this.setState({
                    massageData: res
                })
            })
    }
    seletTime = (e) => {
        console.log(e)
        let type = e.map(a => a.stat).toString()
        this.setState({
            timeSelect: e
        })
        console.log(type)

        switch (type) {
            case "true":
                this.setState({
                    modalEdit: true
                })
                break;
            case "false":
                this.setState({
                    modalAdd: true
                })

                break;
            default:
                break;
        }
    }

    render() {
        return (
            <div >
                <Card style={{ width: "100%", overflowX: "auto" }}>
                    <table className="Table-Main">
                        {this.state.massageData.map(a => (
                            <tr>
                                <td className="Table-collapse Table-Name">{a.doctor_name_prefex} {a.doctor_name_fname} {a.doctor_name_lname}</td>
                                {a.service.map(b => (
                                    <td className="Table-collapse Table-Time" onClick={() => this.seletTime([{
                                        docId: a.doctor_name_id, pointId: a.point_id, timeId: b.timeId, stat: b.dis, serviceId: b.serviceId, docName: a.doctor_name_prefex + ' ' + a.doctor_name_fname + ' ' + a.doctor_name_lname, timeName: b.time, date: b.serviceDate
                                    }])}>
                                        <p>{b.time}</p>
                                        <p>{b.name}</p>
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </table>
                </Card>
                {/*Modal Add*/}
                <Modal
                    title="ทำรายการนัด"
                    name="Add"
                    visible={this.state.modalAdd}
                    onCancel={() => this.setState({ modalAdd: false })}
                >
                    {this.state.timeSelect.map(a => (
                        <Card>
                            <p>ผู้ให้บริการ {a.docName}</p>
                            <p>วันที่ {a.date}</p>
                            <p>เวลา {a.timeName}</p>
                        </Card>
                    ))}

                </Modal>
                {/*Modal Add*/}

                {/*Modal Edit*/}
                <Modal
                    title="แก้ไข"
                    name="Edit"
                    visible={this.state.modalEdit}
                    onCancel={() => this.setState({ modalEdit: false })}
                >

                </Modal>
                {/*Modal Edit*/}
            </div >
        )
    }
}
export default MassageAdmin
