import React, { Component } from 'react'
import { CookiesProvider, withCookies } from 'react-cookie'
import { Card, Input, Button, Col, message, Select, DatePicker, Row } from 'antd'
import AES from 'crypto-js/aes'
import base64 from 'crypto-js/enc-base64'
import CryptoJS from 'crypto-js'
import moment from 'moment'
const { Option } = Select;

class Monitor extends Component {
    constructor() {
        super();
        this.state = {
            point: [],
            empoye: [],
            checkLogin: "",
            selectDate: "",
            selectPoint: []
        }
    }

    componentDidMount() {
        let date = new Date()
        date = `${date.getFullYear()}-${("0" + (date.getMonth() + 1)).slice(-2)}-${date.getDate()}`
        //console.log(date)
        this.setState({
            selectDate: date
        })
        const { cookies } = this.props;
        if (cookies.get('user') === undefined && cookies.get('pass') === undefined) {
            console.log("u")
        }
        else {
            fetch("http://183.88.219.85:7078/appoint/login.php", {
                method: "POST",
                body: JSON.stringify({
                    user: cookies.get('user'),
                    pass: cookies.get('pass')
                })
            })
                .then(res => res.json())
                .then(res => {
                    let checkLogin = res.map(a => a.stat).toString()
                    this.setState({
                        checkLogin: checkLogin,
                        empoye: res
                    })
                })
            fetch("http://183.88.219.85:7078/appoint/service_point.php")
                .then(res1 => res1.json())
                .then(res1 => {
                    this.setState({
                        point: res1
                    })
                })
        }
    }

    componentWillReceiveProps(nextProps) {
        console.log(this.props)
        const { cookies } = this.props;
        //console.log(cookies)
        fetch("http://183.88.219.85:7078/appoint/login.php", {
            method: "POST",
            body: JSON.stringify({
                user: cookies.get('user'),
                pass: cookies.get('pass')
            })
        }).then(res => res.json())
            .then(res => {
                let checkLogin = res.map(a => a.stat).toString()
                this.setState({
                    checkLogin: checkLogin,
                    empoye: res,
                    selectPoint: Number(res.map(a => a.point))
                })
            })
    }

    Login = () => {
        const { cookies } = this.props;
        cookies.remove('user')
        cookies.remove('pass')
        let user = document.getElementById('user').value,
            pass = document.getElementById('pass').value

        user = CryptoJS.enc.Utf8.parse(user)
        user = CryptoJS.enc.Base64.stringify(user)
        pass = CryptoJS.enc.Utf8.parse(pass)
        pass = CryptoJS.enc.Base64.stringify(pass)

        cookies.set('user', user)
        cookies.set('pass', pass)
        //console.log(this.props.cookies.cookies)

        fetch("http://183.88.219.85:7078/appoint/login.php", {
            method: "POST",
            body: JSON.stringify({
                user: user,
                pass: pass
            })
        })
            .then(res => res.json())
            .then(res => {
                let checkLogin = res.map(a => a.stat).toString()
                if (checkLogin === "1") {
                    message.success('success')
                    this.setState({
                        checkLogin: checkLogin,
                        empoye: res
                    })
                } else {
                    message.error('username or password')
                    this.setState({
                        checkLogin: checkLogin
                    })
                }
            })
    }

    delCook = () => {
        const { cookies } = this.props;
        cookies.remove('user')
        cookies.remove('pass')
    }

    dateChange = (a, b) => {
        console.log(b)
        this.setState({
            selectDate: b
        })
    }

    pointChange = (e) => {
        console.log(e)
        this.setState({
            selectPoint: e
        })
    }

    render() {
        let checkLogin = this.state.checkLogin
        if (checkLogin === "" || checkLogin === "0") {
            return (
                <div>
                    <Col lg={{ span: 10, offset: 7 }} style={{ padding: "100px 0px 0px 0px" }}>
                        <Card>
                            <h1 style={{ textAlign: "center" }}>Appointment_Login</h1>
                            <h2>UserName:</h2>
                            <Input id="user" />
                            <h2>Password:</h2>
                            <Input id="pass" type="password" />
                            <br />
                            <br />
                            <Button block size="large" type="primary" onClick={this.Login} >LOGIN</Button>
                            <br />
                            <br />
                            <Button block size="large" type="danger" onClick={this.delCook} >LOGOUT</Button>
                        </Card>
                    </Col>
                </div>
            )
        }
        else if (checkLogin === "1") {
            return (
                <div>
                    <CookiesProvider>
                        <div>
                            <Card >
                                {this.state.empoye.map(a => (
                                    <div>
                                        <Row justify={"space-around"}>
                                            <Col lg={{ span: 24 }}>
                                                <Col lg={{ span: 19 }}>
                                                    <h1 style={{ textAlign: "center" }}>ระบบนัดออนไลน์</h1>
                                                </Col>
                                                <Col lg={{ span: 5 }}>
                                                    <h2 style={{ textAlign: "right" }}>{`${a.person_firstname} ${a.person_lastname} `} <Button style={{ width: "50" }} type="danger" onClick={this.delCook} >LOGOUT</Button></h2>
                                                </Col>
                                            </Col>
                                        </Row>
                                    </div>
                                ))}
                            </Card>

                            <Card>
                                <h1 style={{ textAlign: "center" }}>รายชื่อ</h1>
                                <h3>
                                    จุดบริการ :{" "}
                                    <Select
                                        showSearch
                                        style={{ width: 800 }}
                                        placeholder="Select a point"
                                        value={this.state.selectPoint}
                                        optionFilterProp="children"
                                        onChange={this.pointChange}
                                        filterOption={(input, option) =>
                                            option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                        }
                                    >
                                        {this.state.point.map(a => (
                                            <Option value={a.point_id}>{a.point_name}</Option>
                                        ))}
                                    </Select>
                                    {" "}
                                    วันที่ :{" "}
                                    <DatePicker
                                        value={moment(this.state.selectDate)}
                                        style={{ width: 'auto' }}
                                        onChange={this.dateChange} />
                                </h3>
                            </Card>
                        </div>
                    </CookiesProvider>
                </div>
            )
        }
    }
}

export default withCookies(Monitor)
